import React, { useState, useEffect } from "react";
import "../styles/recommendationsPage.css"; // Separate CSS file for styles
import axios from "axios";

const RecommendationsPage = () => {
  const [products, setProducts] = useState([]); // Store products from API
  const [searchTerm, setSearchTerm] = useState(""); // Search input state
  const [sortOrder, setSortOrder] = useState("asc"); // Sort state
  const [itemsPerPage, setItemsPerPage] = useState(10); // Items per page state
  const [currentPage, setCurrentPage] = useState(1); // Current page state
  const [totalPages, setTotalPages] = useState(0); // Total pages from backend

  // Fetch products when state changes
  useEffect(() => {
    fetchProducts();
  }, [searchTerm, sortOrder, itemsPerPage, currentPage]);

  const fetchProducts = async () => {
    try {
      // Fetch with query parameters for backend filtering, sorting, and pagination
      const response = await axios.get("/api/products", {
        params: {
          search: searchTerm,
          sort: sortOrder,
          limit: itemsPerPage,
          page: currentPage,
        },
      });
      setProducts(response.data.products || []);
      setTotalPages(response.data.totalPages || 0); // Total pages from backend
    } catch (error) {
      console.error("Error fetching products:", error);
      setProducts([]);
    }
  };

  return (
    <div className="recommendations-page">
      {/* Top Section: Search and Sorting */}
      <div className="top-bar">
        {/* Search Bar */}
        <input
          type="text"
          placeholder="Search products..."
          value={searchTerm}
          onChange={(e) => {
            setSearchTerm(e.target.value);
            setCurrentPage(1); // Reset to page 1
          }}
          className="search-input"
        />

        {/* Sorting */}
        <button
          onClick={() =>
            setSortOrder((prev) => (prev === "asc" ? "desc" : "asc"))
          }
          className="sort-button"
        >
          {sortOrder === "asc" ? "⬆️ Sort Asc" : "⬇️ Sort Desc"}
        </button>
      </div>

      {/* Product Cards */}
      <div className="product-list">
        {products.length > 0 ? (
          products.map((product) => (
            <div key={product.id} className="product-card">
              <img
                src={product.imageUrl}
                alt={product.name}
                className="product-image"
              />
              <div className="product-info">
                <h3>{product.name}</h3>
                <p>{product.description}</p>
                <p className="product-cost">Cost: ${product.cost}</p>
                <a
                  href={product.link}
                  target="_blank"
                  rel="noopener noreferrer"
                  className="product-link"
                >
                  View Product
                </a>
              </div>
            </div>
          ))
        ) : (
          <p>No products found.</p>
        )}
      </div>

      {/* Pagination Controls */}
      <div className="pagination-controls">
        {/* Items Per Page */}
        <select
          value={itemsPerPage}
          onChange={(e) => {
            setItemsPerPage(parseInt(e.target.value));
            setCurrentPage(1); // Reset to page 1
          }}
          className="items-per-page"
        >
          <option value={10}>10 per page</option>
          <option value={20}>20 per page</option>
          <option value={30}>30 per page</option>
        </select>

        {/* Page Numbers */}
        <div className="page-numbers">
          {Array.from({ length: totalPages }, (_, index) => (
            <button
              key={index + 1}
              onClick={() => setCurrentPage(index + 1)}
              className={`page-button ${
                currentPage === index + 1 ? "active-page" : ""
              }`}
            >
              {index + 1}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default RecommendationsPage;
