import React, { useState } from "react";
import "react-step-progress-bar/styles.css";
import { ProgressBar, Step } from "react-step-progress-bar";
import "../styles/profileCreation.css";
import { Link } from "react-router-dom";

const ProfileCreation = () => {
  const [step, setStep] = useState(1);
  const [formData, setFormData] = useState({
    skinType: "",
    skinConcerns: [],  // Change to an array to hold multiple concerns
    ageRange: "",
    skincareGoals: [],  // Change to an array to hold multiple goals
    productType: [],    // Change to an array to hold multiple product types
    routineLevel: "",
    allergies: "",
    budget: "",
    preferredIngredients: "",
    avoidedIngredients: "",
    additionalComments: "",
  });
  const [isSubmitted, setIsSubmitted] = useState(false);

  const handleNext = () => {
    if (step < 3) setStep(step + 1);
  };

  const handleBack = () => {
    if (step > 1) setStep(step - 1);
  };

  const handleInputChange = (field, value) => {
    if (Array.isArray(formData[field])) {
      if (formData[field].includes(value)) {
        // Remove the value if it is already selected
        setFormData({
          ...formData,
          [field]: formData[field].filter((item) => item !== value),
        });
      } else {
        // Add the value to the array
        setFormData({
          ...formData,
          [field]: [...formData[field], value],
        });
      }
    } else {
      setFormData({ ...formData, [field]: value });
    }
  };

  const handleSubmit = () => {
    console.log(formData); // Log or send the data to the backend here
    setIsSubmitted(true); // Set submission state to true
  };

  const renderStepContent = () => {
    switch (step) {
      case 1:
        return (
          <div className="form-step">
            <h2>Skin Type & Concerns</h2>
            <div>
              <label>Skin Type:</label>
              <select
                value={formData.skinType}
                onChange={(e) => handleInputChange("skinType", e.target.value)}
              >
                <option value="">Select</option>
                <option value="Dry">Dry</option>
                <option value="Oily">Oily</option>
                <option value="Combination">Combination</option>
                <option value="Normal">Normal</option>
              </select>
            </div>
            <div>
              <label>Skin Concerns:</label>
              <div>
                {["Blemishes", "Acne", "Dark Spots", "Wrinkles", "None"].map(
                  (concern) => (
                    <div key={concern}>
                      <input
                        type="checkbox"
                        id={`concern-${concern}`}
                        value={concern}
                        checked={formData.skinConcerns.includes(concern)}
                        onChange={(e) =>
                          handleInputChange("skinConcerns", concern)
                        }
                      />
                      <label htmlFor={`concern-${concern}`}>{concern}</label>
                    </div>
                  )
                )}
              </div>
            </div>
            <div>
              <label>Age Range:</label>
              <select
                value={formData.ageRange}
                onChange={(e) => handleInputChange("ageRange", e.target.value)}
              >
                <option value="">Select</option>
                <option value="Under 18">Under 18</option>
                <option value="18-25">18-25</option>
                <option value="26-40">26-40</option>
                <option value="40+">40+</option>
              </select>
            </div>
            <div>
              <label>Skincare Goals:</label>
              <div>
                {[
                  "Hydration and Moisture",
                  "Clear and Acne-Free Skin",
                  "Even Skin Tone",
                  "Anti-Aging",
                  "Sun Protection",
                  "Control Oil and Shine",
                  "Calming Sensitive Skin",
                ].map((goal) => (
                  <div key={goal}>
                    <input
                      type="checkbox"
                      id={`goal-${goal}`}
                      value={goal}
                      checked={formData.skincareGoals.includes(goal)}
                      onChange={(e) =>
                        handleInputChange("skincareGoals", goal)
                      }
                    />
                    <label htmlFor={`goal-${goal}`}>{goal}</label>
                  </div>
                ))}
              </div>
            </div>
            <div>
              <label>Preferred Product Type:</label>
              <div>
                {["Serums", "Creams", "Toners"].map((product) => (
                  <div key={product}>
                    <input
                      type="checkbox"
                      id={`product-${product}`}
                      value={product}
                      checked={formData.productType.includes(product)}
                      onChange={(e) =>
                        handleInputChange("productType", product)
                      }
                    />
                    <label htmlFor={`product-${product}`}>{product}</label>
                  </div>
                ))}
              </div>
            </div>
            <div>
              <label>Current Skincare Routine:</label>
              <select
                value={formData.routineLevel}
                onChange={(e) =>
                  handleInputChange("routineLevel", e.target.value)
                }
              >
                <option value="">Select</option>
                <option value="Basic (Cleanser, Moisturizer)">Basic</option>
                <option value="Intermediate (Includes Serums, Sunscreen)">
                  Intermediate
                </option>
                <option value="Advanced (Includes Toners, Masks, etc.)">
                  Advanced
                </option>
              </select>
            </div>
          </div>
        );
      case 2:
        return (
          <div className="form-step">
            <h2>Preferences & Allergies</h2>
            <div>
              <label>Known Allergies or Sensitivities:</label>
              <input
                type="text"
                placeholder="E.g., Parabens, Fragrances"
                value={formData.allergies}
                onChange={(e) => handleInputChange("allergies", e.target.value)}
              />
            </div>
            <div>
              <label>Budget (INR):</label>
              <input
                type="number"
                placeholder="Enter your budget"
                value={formData.budget}
                onChange={(e) => handleInputChange("budget", e.target.value)}
              />
            </div>
            <div>
              <label>Preferred Ingredients:</label>
              <input
                type="text"
                placeholder="E.g., Aloe Vera, Vitamin C"
                value={formData.preferredIngredients}
                onChange={(e) =>
                  handleInputChange("preferredIngredients", e.target.value)
                }
              />
            </div>
            <div>
              <label>Avoided Ingredients:</label>
              <input
                type="text"
                placeholder="E.g., Sulfates, Parabens"
                value={formData.avoidedIngredients}
                onChange={(e) =>
                  handleInputChange("avoidedIngredients", e.target.value)
                }
              />
            </div>
          </div>
        );
      case 3:
        return (
          <div className="form-step">
            <h2>Additional Comments</h2>
            <div>
              <textarea
                placeholder="Enter any specific notes or requirements"
                value={formData.additionalComments}
                onChange={(e) =>
                  handleInputChange("additionalComments", e.target.value)
                }
              ></textarea>
            </div>
          </div>
        );
      default:
        return null;
    }
  };

  return (
    <div className="profile-creation">
      {!isSubmitted ? ( // Render the form if not submitted
        <>
          <ProgressBar
            percent={(step / 3) * 100}
            filledBackground="linear-gradient(to right, #fefb72, #f0bb31)"
          >
            <Step transition="scale">
              {({ accomplished }) => (
                <div className={`step ${accomplished ? "completed" : ""}`}>1</div>
              )}
            </Step>
            <Step transition="scale">
              {({ accomplished }) => (
                <div className={`step ${accomplished ? "completed" : ""}`}>2</div>
              )}
            </Step>
            <Step transition="scale">
              {({ accomplished }) => (
                <div className={`step ${accomplished ? "completed" : ""}`}>3</div>
              )}
            </Step>
          </ProgressBar>
          <div className="form-content">{renderStepContent()}</div>
          <div className="form-navigation">
            {step > 1 && <button onClick={handleBack}>Back</button>}
            {step < 3 && <button onClick={handleNext}>Next</button>}
            {step === 3 && <button onClick={handleSubmit}>Submit</button>}
          </div>
        </>
      ) : (
        // Render confirmation message after submission
        <div className="submission-message">
          <h2>Profile Creation Completed!</h2>
          <p>Your profile has been successfully created. </p>
          <p>
            You can now check out your personalized{" "}
            <Link to="/recommendations">Recommendations Page</Link>.
          </p>
        </div>
      )}
    </div>
  );
};

export default ProfileCreation;
